c_a2967956(0x4a53136b2d7148ce); /* line */ 
return 0; // Auction has concluded
} else {c_a2967956(0x194dfa7dff97fea1); /* branch */ 

c_a2967956(0xa058549da0371770); /* statement */ 
c_a2967956(0xd0650cbd20baea02); /* line */ 
uint256 remainingBlocks = auctionEndBlockNumber - block.number;
c_a2967956(0xdb55741d290899e6); /* statement */ 
c_a2967956(0xc10056326256d777); /* line */ 
return startPrice - remainingBlocks * priceDecrementPerBlock;
}
}
