c_a2967956(0xbbd293b4ff629c23); /* line */ 
uint256 refundAmount = bidsByAddress[msg.sender];
c_a2967956(0x0f4e1b5f75ac2c1f); /* line */ 
bidsByAddress[msg.sender] = 0;
c_a2967956(0x8383411590e4fb0d); /* statement */ 
c_a2967956(0xd2461fa2fb9c4605); /* line */ 
payable(msg.sender).transfer(refundAmount);
}

/*

getBalance allows to query the balance of a specific account.
*/
function getBalance(address account) external view returns (uint256) {c_a2967956(0x540e7f33a3d69e6b); /* function */ 

c_a2967956(0xaf94bd167851dccc); /* statement */ 
c_a2967956(0x6c44be705788bbda); /* line */ 
return bidsByAddress[account];
}
